sp_widget.client_script:
function($scope, spUtil, $location) {

	var c = this;

	$scope.$on('data_table.click', function(e, parms){
		var s = {id: 'hri_contact_form', table: parms.table, sys_id: parms.sys_id, view: 'sp'};
		$location.search(s);
	});

}

sp_widget.script:
(function() {
